package practice321;

public class numbrt {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<=4;i++) {
			for(int j=1;j<=7;j++) {
				if( j>=5-i&& j<=i+3) {
				System.out.print(j);
			}else if(i==4){
				System.out.print(i);
			}else {
				System.out.print(" ");
			}
				
			
	}
			System.out.println();
			

}
}
}