package pattern;

public class k1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
for( int i=0;i<5;i++) {
	for(int j=5-i;j>1;j--) {
		System.out.print(" ");
	
}//*_*_for space 
for( int j=0;j<=i;j++)
	 
{
	System.out.print(" *");
	//star
}
System.out.println();
	}

}
}